getgenv().ExecutedcircleCMD=true;getgenv().circleCMD=function()game.Players.LocalPlayer.Character.HumanoidRootPart.Anchored=false;getgenv().CA_IsFreezing=false;getgenv().CA_IsAirlocking=false;getgenv().CA_IsGraving=false;local a=0;for b,c in pairs(game.Players:GetPlayers())do for d,e in pairs(alts)do if e==c.UserId then a=a+1 end end end;if args[2]then if MUF_ReturnClosestPlayer(args[2])then local f=MUF_ReturnClosestPlayer(args[2]).Character:FindFirstChild('HumanoidRootPart')local g=2*math.pi;local h=10;local function i(j)local k=math.cos(j)*h;local c=math.sin(j)*h;return k,c end;local j=math.random(1,tonumber(a))*g/tonumber(a)local k,c=i(j)local l=(f.CFrame*CFrame.new(k,0,c)).p;local m=f.Position;game.Players.LocalPlayer.Character:FindFirstChild('HumanoidRootPart').CFrame=CFrame.new(l,m)wait()game.Players.LocalPlayer.Character.HumanoidRootPart.Anchored=true;if ChatMessages then MUF_sendChatMessage('Circled around '..MUF_ReturnClosestName(args[2])..'!')end else if ChatMessages then MUF_sendChatMessage('Unknown Player')end end else local f=game:GetService("Players"):GetPlayerByUserId(controller).Character:FindFirstChild('HumanoidRootPart')local g=2*math.pi;local h=10;local function i(j)local k=math.cos(j)*h;local c=math.sin(j)*h;return k,c end;local j=math.random(1,tonumber(a))*g/tonumber(a)local k,c=i(j)local l=(f.CFrame*CFrame.new(k,0,c)).p;local m=f.Position;game.Players.LocalPlayer.Character:FindFirstChild('HumanoidRootPart').CFrame=CFrame.new(l,m)wait()game.Players.LocalPlayer.Character.HumanoidRootPart.Anchored=true;if ChatMessages then MUF_sendChatMessage('Circled around controller!')end end end
