getgenv().ExecutedcircleCMD=true;getgenv().circleCMD=function()game.Players.LocalPlayer.Character.HumanoidRootPart.Anchored=false;getgenv().CA_IsFreezing=false;getgenv().CA_IsAirlocking=false;getgenv().CA_IsGraving=false;if args[2]then if MUF_ReturnClosestPlayer(args[2])then local a=MUF_ReturnClosestPlayer(args[2]).Character:FindFirstChild('HumanoidRootPart')local b=2*math.pi;local c=10;local function d(e)local f=math.cos(e)*c;local g=math.sin(e)*c;return f,g end;local e=math.random(1,tonumber(maxAltsNumber))*b/tonumber(maxAltsNumber)local f,g=d(e)local h=(a.CFrame*CFrame.new(f,0,g)).p;local i=a.Position;game.Players.LocalPlayer.Character:FindFirstChild('HumanoidRootPart').CFrame=CFrame.new(h,i)wait()game.Players.LocalPlayer.Character.HumanoidRootPart.Anchored=true;if ChatMessages then MUF_sendChatMessage('Circled around '..MUF_ReturnClosestName(args[2])..'!')end else if ChatMessages then MUF_sendChatMessage('Unknown Player')end end else local a=game:GetService("Players"):GetPlayerByUserId(controller).Character:FindFirstChild('HumanoidRootPart')local b=2*math.pi;local c=10;local function d(e)local f=math.cos(e)*c;local g=math.sin(e)*c;return f,g end;local e=math.random(1,tonumber(maxAltsNumber))*b/tonumber(maxAltsNumber)local f,g=d(e)local h=(a.CFrame*CFrame.new(f,0,g)).p;local i=a.Position;game.Players.LocalPlayer.Character:FindFirstChild('HumanoidRootPart').CFrame=CFrame.new(h,i)wait()game.Players.LocalPlayer.Character.HumanoidRootPart.Anchored=true;if ChatMessages then MUF_sendChatMessage('Circled around controller!')end end end
